Page._content:
@font-face {font-family: 'iconfont';
    src: url('../fonts/iconfont.eot'); /* IE9*/
    src: url('../fonts/iconfont.eot?#iefix') format('embedded-opentype'), /* IE6-IE8 */
    url('../fonts/iconfont.woff') format('woff'), /* chrome、firefox */
    url('../fonts/iconfont.ttf') format('truetype'), /* chrome、firefox、opera、Safari, Android, iOS 4.2+*/
    url('../fonts/iconfont.svg#iconfont') format('svg'); /* iOS 4.1- */
}


.icon-work:before { content: "\eac9"; }
.icon-project:before { content: "\eaf4"; }
.icon-phone:before { content: "\ec7a"; }
.icon-stars:before { content: "\ed32"; }
.icon-star:before { content: "\ed1d"; }
.icon-person:before { content: "\ec88"; }
.icon-librarybooks:before { content: "\ebf9"; }
.icon-education:before { content: "\ebca"; }
.icon-create:before { content: "\eaf8"; }
.icon-call:before { content: "\eab6"; }
.icon-weixin:before { content: "\3488"; }
.icon-build:before { content: "\eaa2"; }
.icon-edit:before { content: "\eb31"; }
.icon-flower:before { content: "\ec0a"; }
.icon-playlistadd:before { content: "\ecae"; }
.icon-delete:before { content: "\e61b"; }
.icon-trophy:before { content: "\e80b"; }
.icon-teaching:before { content: "\eb6f"; }
span {
    display: inline-block;
}

.label-value{
    width: 200px;
    vertical-align: top;
}

.iconfont {
    font-family:"iconfont" !important;
    font-size:1.1em;font-style:normal;
    color:inherit;
    -webkit-font-smoothing: antialiased;
    -webkit-text-stroke-width: 0.2px;
    -moz-osx-font-smoothing: grayscale;
}

* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    margin: 0; 
}

h1, h2, h3, h4, h5 {
    font-weight: 500;
    color: #607d8b;
    line-height: 1.5;
}

.progress-list div {
    width: 100%;
    margin-left: 200px;
}

.info-list, .progress-list{
    list-style: none;
    padding-left: 0px;
    font-size: 16px;
    line-height: 2;
}

.stack-list {
    list-style: none;
    padding-left: 0px;
    line-height: 1.5;
}

.stack-list li {
    margin-bottom: 12px;
}

.right-list li {
    margin-bottom: 12px;
}


hr {
    margin-bottom: 8px;
}

.info-unit {
    color: #666;
    margin-bottom: 20px;
}

.remodal-img h3 {
    float: left;
    margin-bottom: 10px;
}

.remodal-img input {
    float: left;
    width: 480px;
    height: 42px;
    font-size: 20px;
    padding: 6px;
    color: #666;

}

.remodal-img button {
    float: right;
    background: #607d8b;

}

.remodal-img button:hover{
    float: right;
    background: #607d8b;

}


body {
    background-color: lightslategray;
    font-family: sans-serif, Microsoft Yahei, Helvetica Neue;
    zoom: 80%;
}

footer {
}

p {
    line-height: 1.5;
}

progress {
    background-color: #f6f6f6;
    display: inline;
    /*width: 195px;*/
    height: 1em;

}

progress::-webkit-progress-bar {
    background-color: #e0eaf0;
}

progress::-webkit-progress-value {
    background-color: #708d9b;
}

progress::-moz-progress-bar {
    background-color: #e0eaf0;

}

progress::-moz-progress-value {
    background-color: #708d9b;
}

.progress-list {
    line-height: 2;
}



.me {
    text-align: center;
    margin-bottom: 20px;
}

.weixin{
    margin-top: 20px;
    background-color: #e0eaf0;   
    position: relative;
    padding-top: 40px;
    padding-bottom: 40px;
    text-align: center;
}

.weixin img{
    width: 200px;
}

.left-label {
    display: inline-block;
    width: 120px;
}

.portrait {
    margin: 0 auto;
    margin-top:20px;
    margin-bottom: 10px;
    background-image:url(./bg.jpg);
    background-size: 120%;
    background-position:center;
    border-radius:50%;
    border:5px solid #607d8b;
    width:160px;
    height:160px;
}


.container {
    position: relative;
    margin: 0 auto;

    width: 1050px;
    height: 3350px;
    background-color: white;

}

.side {
    position: absolute;
    width: 360px;
    height: 100%;
    background-color: whitesmoke;
    padding-left: 20px;
    padding-right: 20px;
}

.main {

    position: relative;;
    margin-left: 360px;
    min-height: 100%;
    padding: 20px;

}

.info-header:hover {
    background-color: #d4dee4;
}

.info-unit ul li:hover {
    background-color: #d4dee4;
}

.info-header .unit-remove {
    float: right;
    margin-left: 20px;
    visibility: hidden;
    cursor: pointer;

}

.info-header .item-add {
    float: right;
    margin-left: 20px;
    margin-right: 2px;
    visibility: hidden;
    cursor: pointer;

}

 .item-remove {
    position: absolute;
    right: 2px;
    margin-right: 2px;
    visibility: hidden;
    cursor: pointer;

}

ul li{
    position: relative;
}

 .experience-list .item-remove {
    position: absolute;
    right: 2px;
    top: 0px;
    margin-right: 2px;
    visibility: hidden;
    cursor: pointer;

}

 .stack-list .item-remove {
    position: absolute;
    right: 2px;
    top: 0px;
    margin-right: 2px;
    visibility: hidden;
    cursor: pointer;

}

